mapper.select:
select

from chess_card_record
where 1=1

  AND BIG_FIELD = #{bigField,jdbcType=TINYINT}


  AND SMALL_FIELD = #{smallField,jdbcType=TINYINT}


  AND LEVELS = #{levels,jdbcType=TINYINT}


  AND POPULATION = #{population,jdbcType=TINYINT}


  AND
  ( ROLE_1 = #{role1,jdbcType=SMALLINT} OR
    ROLE_2 = #{role1,jdbcType=SMALLINT} OR
    ROLE_3 = #{role1,jdbcType=SMALLINT} OR
    ROLE_4 = #{role1,jdbcType=SMALLINT} )
